import React, { useState } from 'react';
import './App.css'; // Impor file CSS untuk gaya

function App() {
  // Membuat state untuk menyimpan nilai input
  const [inputValue, setInputValue] = useState('');
  // Membuat state untuk menyimpan nilai hasil input yang akan ditampilkan
  const [resultValue, setResultValue] = useState('');

  // Handler untuk mengubah nilai input saat perubahan terjadi
  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  // Handler untuk menampilkan nilai input saat tombol ditekan
  const handleButtonClick = () => {
    if (inputValue.trim() !== '') {
      setResultValue(inputValue); // Menyimpan nilai input ke state hasil
      setInputValue(''); // Mengosongkan nilai input
    } else {
      alert('Masukkan nilai sebelum menampilkan!');
    }
  };

  return (
    <div className="container">
      <h2>Masukkan Nilai</h2>
      {/* Label untuk input */}
      <label htmlFor="inputField">Nilai:</label>
      {/* Input untuk memasukkan nilai */}
      <input
        id="inputField"
        type="text"
        value={inputValue}
        onChange={handleInputChange} // Memanggil handler saat terjadi perubahan input
      />
      {/* Tombol untuk menampilkan nilai input */}
      <button onClick={handleButtonClick}>Tampilkan Nilai Input</button>
      {/* Menampilkan hasil input */}
      <div className="result">
        {resultValue && <p>Nilai input adalah: {resultValue}</p>}
      </div>
    </div>
  );
}

export default App;
