import React, { Component } from 'react';
import Operan from './Operan';

export default class StateProps extends Component {
    constructor(props){
        super(props);
        this.state = {
            makanan : 'Bakso'
        }
    }

    // Metode untuk menangani perubahan makanan
    gantiMakanan = () => {
        // Mengubah state makanan menjadi "Soto" saat tombol diklik
        this.setState({ makanan: "Soto" });
    }

    render() {
        return (
            <div>
                <h1>{this.state.makanan}</h1>
                {/* Menggunakan event handler onClick untuk menangani klik tombol */}
                <button onClick={this.gantiMakanan}>Ganti Makanan</button>
                {/* Mengirimkan state makanan ke komponen Operan */}
                <Operan makanan={this.state.makanan} />
            </div>
        );
    }
}
